import { createStore } from "vuex";

import { SidebarStates,SidebarMutations,SidebarActions } from "./modules/SidebarStore";
// import { NetworkStates,NetworkMutations,NetworkActions,NetworkGetters } from "./modules/Network";
import { GameUtilsStates,GameUtilsMutations,GameUtilsActions,GameUtilsGetters } from "./modules/GameUtilsStore";
export default createStore({
    state:{
        Overlay:false,
        theme: localStorage.getItem('theme') || 'dark',
        ...SidebarStates,
        // ...NetworkStates,
        ...GameUtilsStates,
    },
    mutations:{
        setOverlay(state,value){
            state.Overlay = value;
        },
        setTheme(state, theme) {
            state.theme = theme;
            localStorage.setItem('theme', theme); 
        },
        ...SidebarMutations,
        // ...NetworkMutations,
        ...GameUtilsMutations,
    },
    actions:{
        onOverlay({commit}){
            commit('setOverlay',true)
        },
        offOverlay({commit}){
            commit('setOverlay',false)
            commit('setActiveSidebar',false)
            commit('setSidebarAnimation',false)
        },
        toggleTheme({ commit }, theme) {
            commit('setTheme', theme); 
        },
        ...SidebarActions,
        // ...NetworkActions,
        ...GameUtilsActions,
    },
    getters:{
        currentTheme(state) {
            return state.theme; 
        },
        activeOverlay(state){
            return state.Overlay;
        },
        // ...NetworkGetters,
        ...GameUtilsGetters,
    }
})
