export const SidebarStates = {
    ActiveSidebar:false,
    SidebarAnimation:false,
}
export const SidebarMutations = {
    setActiveSidebar(state,value){
        state.ActiveSidebar = value;
    },
    setSidebarAnimation(state,value){
        state.SidebarAnimation = value;
    }
}
export const SidebarActions = {
    onSidebar({commit}){
        commit('setActiveSidebar',true)
        setTimeout(() => commit('setSidebarAnimation',true),100)
    },
}
