export const GameUtilsStates = {
    HtmlViewInfo: JSON.parse(sessionStorage.getItem('game-record-info')) || {}
}

export const GameUtilsMutations = {
    setHVI(state, payload) {
        state.HtmlViewInfo = payload;
        sessionStorage.setItem('game-record-info', JSON.stringify(payload)); 
    }
}

export const GameUtilsActions = {
    toggleHVI({ commit }, payload) {
        commit('setHVI', payload);
    }
}

export const GameUtilsGetters = {
    getHVI(state) {
        return state.HtmlViewInfo;
    }
}
