import { Preferences } from "@capacitor/preferences";

export const recordAttempt = async (keyName) => {
  try {
    let AttemptContent = await Preferences.get({ key: keyName });

    let attemptCount = 1;
    if (AttemptContent.value) {
      fileContent = JSON.parse(fileContent.value);
      attemptCount = fileContent.attempt + 1;
    }
    
    const attemptData = {
      attempt: attemptCount,
      date: new Date().toISOString(),
    };

    await Preferences.set({
      key: keyName,
      value: JSON.stringify(attemptData),
    });
    console.log('Attempt recorded successfully');
  } catch (err) {
    console.error('Error recording attempt:', err);
  }
};

export const savePreference = async (id, payload) => {
  await Preferences.set({
    key: id,  
    value: JSON.stringify(payload),  
  });
};

export const getPreference = async (id) => {
  const { value } = await Preferences.get({ key: id });
  return value ? JSON.parse(value) : null;  
};

export const removePreference = async (id) => {
  await Preferences.remove({ key: id });
};

export const getAllKeys = async () => {
  const { keys } = await Preferences.keys();
  return keys;
};
